    if (state === 'O') {backgroundColor = 'blackBackground'};
    // use setState in an arrow function on div element
    return (<div onClick={() => {
        if (state === 'X') {
            setState('O')
        } else {setState('X')
    }}} id="Square" class={backgroundColor}>
        {props.name}
    </div>)
}
